import React, { useEffect, useState } from "react";
import "../../css/PatientProfile.css";
import api from "../../api"

const PatientProfile = () => {
  const [patient , setPatient ] = useState({});

  useEffect(()=>{
    api.get("http://localhost:8080/patient/profile", { withCredentials: true })
    .then(res =>
      setPatient(res)
    )
    .catch(err => {
      console.error("Error fetching dashboard data:", err);
    })
  },[])
  console.log(patient.data);


  const getInitials = (firstName, lastName) => {
    const firstInitial = firstName ? firstName.charAt(0).toUpperCase() : "";
    const lastInitial = lastName ? lastName.charAt(0).toUpperCase() : "";
    return `${firstInitial}${lastInitial}`;
  };

  const fullName = `${patient.data.firstName} ${patient.data.lastName}`;

  return (
    <div className="profile-container">
      <h2 className="profile-title">ðŸ‘¤ Patient Profile</h2>

      <div className="profile-avatar-section">
        <div className="profile-avatar">{getInitials(fullName)}</div>
        <h3 className="avatar-name">{fullName}</h3>
        <button className="update-btn">Update Profile</button>
      </div>

      <div className="profile-card">
        <div className="profile-row">
          <span className="profile-label">Email:</span>
          <span className="profile-value">{patient.data.email}</span>
        </div>
        <div className="profile-row">
          <span className="profile-label">Phone:</span>
          <span className="profile-value">{patient.data.contactNo}</span>
        </div>
        <div className="profile-row">
          <span className="profile-label">Gender:</span>
          <span className="profile-value">{patient.data.gender}</span>
        </div>
        <div className="profile-row">
          <span className="profile-label">Date of Birth:</span>
          <span className="profile-value">{patient.data.dob}</span>
        </div>
        <div className="profile-row">
          <span className="profile-label">Blood Group:</span>
          <span className="profile-value">{patient.data.bloodGroup}</span>
        </div>
        <div className="profile-row">
          <span className="profile-label">Address:</span>
          <span className="profile-value">{patient.data.address}</span>
        </div>
      </div>
    </div>
  );
};

export default PatientProfile;
