import React, { useState } from "react";
import RegisterDoctor from "./RegisterDoctor";
import AdminRequests from "./AdminRequests";
import DoctorsList from "./DoctorsList";
import PatientsList from "./PatientsList";
import AssignPatient from "./AssignPatient";
import DoctorHistory from "./DoctorHistory"; // optional dynamic
import StatsOverview from "./StatsOverview"; // separate stats component

const AdminDashboard = () => {
  const [activeTab, setActiveTab] = useState("stats");
  const [selectedDoctorId, setSelectedDoctorId] = useState(null);

  const renderContent = () => {
    switch (activeTab) {
      case "stats":
        return <StatsOverview />;
      case "registerDoctor":
        return <RegisterDoctor />;
      case "adminRequests":
        return <AdminRequests />;
      case "users":
        return <UsersList />;
      case "doctors":
        return (
          <DoctorsList onHistoryClick={(id) => {
            setSelectedDoctorId(id);
            setActiveTab("doctorHistory");
          }} />
        );
      case "patients":
        return <PatientsList />;
      case "assignPatient":
        return <AssignPatient />;
      case "doctorHistory":
        return <DoctorHistory doctorId={selectedDoctorId} />;
      default:
        return <StatsOverview />;
    }
  };

  return (
    <div className="admin-dashboard">
      {/* Sidebar or Top Nav */}
      <nav className="sidebar">
        <h2>Admin Panel</h2>
        <button onClick={() => setActiveTab("stats")}>üìä Dashboard</button>
        <button onClick={() => setActiveTab("registerDoctor")}>‚ûï Register Doctor</button>
        <button onClick={() => setActiveTab("adminRequests")}>üì• Admin Requests</button>
        <button onClick={() => setActiveTab("users")}>üë• Users</button>
        <button onClick={() => setActiveTab("doctors")}>üßë‚Äç‚öïÔ∏è Doctors</button>
        <button onClick={() => setActiveTab("patients")}>üßë‚Äçü¶Ω Patients</button>
        <button onClick={() => setActiveTab("assignPatient")}>üîÅ Assign Patient</button>
      </nav>

      {/* Main Content */}
      <main className="dashboard-content">{renderContent()}</main>
    </div>
  );
};

export default AdminDashboard;
