import React, { useState, useEffect } from "react";
import "/Users/alokwaigaonkar/health_care/src/css/navbar.css";
import Logo from "../images/logo.png";
import Data from "../data"; // Importing the patient data
import PatientCard from "./PatientCard";
import { useNavigate } from "react-router-dom";

export default function Navbar() {
  const navigate = useNavigate();
  const [text, setText] = useState("");
  const [searchResults, setSearchResults] = useState([]); // Store search results
  const [submittedResults, setSubmittedResults] = useState([]); // Store results after submit
  const [errorClass, setErrorClass] = useState(""); // For shake animation

  // Clearing history after logging out
  useEffect(() => {
    window.history.replaceState(null, null, "/");
  }, []);

  // Handle input change & dynamic search
  const handleInputChange = (event) => {
    const searchQuery = event.target.value;
    setText(searchQuery);

    if (searchQuery.trim() === "") {
      setSearchResults([]);
      return;
    }

    const filteredResults = Data.filter((patient) =>
      patient.name.toLowerCase().includes(searchQuery.toLowerCase())
    );

    setSearchResults(filteredResults);
  };

  // Handle log out
  const handleLogOut = () => {
    navigate("/");
  };

  // Handle form submission
  const handleOnSubmit = (event) => {
    event.preventDefault();

    if (text.trim() === "") {
      setSubmittedResults([]);
      return;
    }

    const filteredResults = Data.filter((patient) =>
      patient.name.toLowerCase().includes(text.toLowerCase())
    );

    setSubmittedResults(filteredResults);

    if (filteredResults.length === 0) {
      setErrorClass("error-box shake");
      setTimeout(() => setErrorClass("error-box"), 500); // Remove shake effect after animation
    }
  };

  return (
    <div>
      <nav className="navbar">
        <a className="navbar-brand" href="/">
          <img src={Logo} alt="Logo" style={{ width: "80px" }} />
        </a>

        <form role="search" onSubmit={handleOnSubmit}>
          <input
            className="input-section"
            type="search"
            placeholder="Patient Name"
            aria-label="Search"
            value={text}
            onChange={handleInputChange}
          />
          <button type="submit" className="btn nav-btn btn-outline-success m-4">
            Submit
          </button>
        </form>
        <button className="nav-btn btn btn-outline-success" onClick={handleLogOut}>
          Log out
        </button>
      </nav>

      {/* Display search results */}
      <div className="search-results">
        {searchResults.length > 0 ? (
          searchResults.map((patient, index) => (
            <PatientCard
              key={index}
              name={patient.name}
              contact={patient.contact}
              email={patient.email}
              gender={patient.gender}
            />
          ))
        ) : text ? (
          <div className={errorClass}>
            <p>No Patient Found</p>
          </div>
        ) : null}
      </div>
    </div>
  );
}
