import React from "react";
import "./PatientRecords.css";
import Data from "../../data";
import { motion } from "framer-motion";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  Tooltip,
  ResponsiveContainer,
  CartesianGrid
} from "recharts";

const generateSampleECG = () => {
  const data = [];
  for (let i = 0; i <= 30; i++) {
    const y =
      Math.sin(i * 0.5) * 30 +
      (Math.random() * 10 - 5) +
      (i % 10 === 0 ? Math.random() * 50 + 20 : 0);
    data.push({ x: i, y });
  }
  return data;
};

const PatientRecords = () => {
  return (
    <div className="patient-records-container">
      {Data.map((patient, index) => (
        <motion.div
          key={index}
          className="record-card"
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5, delay: index * 0.1 }}
        >
          <h2 className="patient-name">{patient.name}</h2>
          <div className="patient-info">
            <p><strong>Gender:</strong> {patient.gender}</p>
            <p><strong>Contact:</strong> {patient.contact}</p>
          </div>

          <div className="record-info">
            <p><strong>ECG:</strong> {patient.medical_records.ECG}</p>
            <p><strong>Heart Rate:</strong> {patient.medical_records.heart_rate} bpm</p>
            <p><strong>Body Temp:</strong> {patient.medical_records.body_temperature} °F</p>
            <p><strong>Room Temp:</strong> {patient.medical_records.room_temperature} °F</p>
            <p><strong>Oxygen Level:</strong> {patient.medical_records.O2_level}%</p>
          </div>

          <div className="ecg-graph">
            <h4>ECG Graph</h4>
            <ResponsiveContainer width="100%" height={150}>
              <LineChart data={generateSampleECG()}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="x" hide />
                <YAxis hide domain={['auto', 'auto']} />
                <Tooltip />
                <Line type="monotone" dataKey="y" stroke="#8884d8" strokeWidth={2} dot={false} />
              </LineChart>
            </ResponsiveContainer>
          </div>
        </motion.div>
      ))}
    </div>
  );
};

export default PatientRecords;
