import React, { useState, useEffect } from "react";
import PatientCard from "./PatientCard";
import api from "../../api"; // import your api.js

export default function PatientList() {
  const [patients, setPatients] = useState([]);
  const [selectedPatient, setSelectedPatient] = useState(null);

  const doctorId = localStorage.getItem("doctorId");
  console.log(doctorId);

  useEffect(() => {
    const fetchPatients = async () => {
      try {
        const res = await api.get(`/doctor/${doctorId}/patients`);
        setPatients(res.data); // adapt if your API wraps the array in { data: [...] }
      } catch (error) {
        console.error("Failed to fetch patients:", error);
        setPatients([]); // Optionally set to empty array on error
      }
    };

    if (doctorId) {
      fetchPatients();
    }
  }, [doctorId]);

  return (
    <div>
      <div className="patient-list row" style={{ marginTop: "4rem" }}>
        {patients.map((patient, index) => (
          <PatientCard
            key={index}
            name={patient.name}
            contact={patient.contact}
            email={patient.email}
            gender={patient.gender}
          />
        ))}
      </div>
    </div>
  );
}
