import React, { useEffect, useState } from "react";
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from "recharts";
import { motion } from "framer-motion";
import "./PatientRecords.css"; // Custom CSS file for styling
import Navbar from "./Navbar";

// Dummy patient data (Replace with API data when backend is integrated)
const dummyPatientData = [
  {
    timestamp: "2025-04-07 10:00:00",
    heartRate: 72,
    bodyTemp: 36.6,
    roomTemp: 24.0,
    ecg: [0.85, 0.87, 0.89, 0.92, 0.91, 0.89, 0.87, 0.90, 0.88, 0.86, 0.85, 0.83],
  }
];

const PatientRecords = () => {
  const [patientData, setPatientData] = useState([]);

  useEffect(() => {
    // TODO: Replace this with real API call
    // fetch("/api/patient-records")
    //   .then((res) => res.json())
    //   .then((data) => setPatientData(data))
    //   .catch((err) => console.error(err));

    // Using dummy data for now
    setPatientData(dummyPatientData);
  }, []);

  return (
    <div>
        <Navbar />
        <div className="patient-records-container">

          {patientData.map((record, index) => (
            <motion.div
              key={index}
              className="record-card"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.4, delay: index * 0.1 }}
            >
              <div className="record-content">
                <h2 className="record-title">Time: {record.timestamp}</h2>
                <p className="record-info">Heart Rate: {record.heartRate} bpm</p>
                <p className="record-info">Body Temp: {record.bodyTemp} °C</p>
                <p className="record-info">Room Temp: {record.roomTemp} °C</p>
                <div className="ecg-chart">
                  <ResponsiveContainer width="100%" height="100%">
                    <LineChart data={record.ecg.map((val, i) => ({ time: i, value: val }))}>
                      <CartesianGrid strokeDasharray="3 3" />
                      <XAxis dataKey="time" />
                      <YAxis domain={[0.8, 1.0]} />
                      <Tooltip />
                      <Line type="monotone" dataKey="value" stroke="#ef4444" strokeWidth={2} dot={false} />
                    </LineChart>
                  </ResponsiveContainer>
                </div>
              </div>
            </motion.div>
          ))}
        </div>
    </div>
  );
};

export default PatientRecords;
