import React, { useEffect, useState } from "react";
import Navbar from "../MainPage/Navbar";
// import axios from "axios";
import "../../css/Patien";

export default function PatientDashboard() {
  const [patientName, setPatientName] = useState("Alok");
  const [appointments, setAppointments] = useState([]);
  const [prescriptions, setPrescriptions] = useState([]);
  const [notifications, setNotifications] = useState([]);
  const [billingHistory, setBillingHistory] = useState([]);

  useEffect(() => {
    // Dummy data for frontend
    setAppointments([
      { date: "2025-04-10", doctor: "Dr. Mehta", department: "Cardiology" },
      { date: "2025-04-15", doctor: "Dr. Singh", department: "Dermatology" },
    ]);

    setPrescriptions([
      { date: "2025-03-20", file: "prescription1.pdf" },
      { date: "2025-03-30", file: "prescription2.pdf" },
    ]);

    setNotifications([
      { message: "New test result uploaded." },
      { message: "Appointment with Dr. Mehta tomorrow at 10AM." },
    ]);

    setBillingHistory([
      { date: "2025-03-15", amount: "‚Çπ1200", service: "General Checkup" },
      { date: "2025-03-28", amount: "‚Çπ900", service: "Blood Test" },
    ]);

    // Backend fetch (Uncomment when backend is ready)
    /*
    axios.get("http://localhost:8080/api/patient/dashboard", { withCredentials: true })
      .then(res => {
        setPatientName(res.data.name);
        setAppointments(res.data.appointments);
        setPrescriptions(res.data.prescriptions);
        setNotifications(res.data.notifications);
        setBillingHistory(res.data.billing);
      })
      .catch(err => console.log(err));
    */
  }, []);

  return (
    <div>
      <Navbar />
      <div className="dashboard-container">
        <h2 className="welcome">Welcome, {patientName} üëã</h2>

        <div className="dashboard-section">
          <h3>üìÅ Health Records Summary</h3>
          <p>You have {prescriptions.length} recent prescriptions.</p>
        </div>

        <div className="dashboard-section">
          <h3>üìÖ Upcoming Appointments</h3>
          <ul>
            {appointments.map((apt, i) => (
              <li key={i}>{apt.date} - {apt.doctor} ({apt.department})</li>
            ))}
          </ul>
        </div>

        <div className="dashboard-section">
          <h3>‚ûï Book New Appointment</h3>
          <button className="btn btn-primary">Book Now</button>
        </div>

        <div className="dashboard-section">
          <h3>üìú Prescriptions & Downloads</h3>
          <ul>
            {prescriptions.map((pres, i) => (
              <li key={i}><a href={`#`}>{pres.file}</a> ({pres.date})</li>
            ))}
          </ul>
        </div>

        <div className="dashboard-section">
          <h3>üîî Notifications & Alerts</h3>
          <ul>
            {notifications.map((note, i) => (
              <li key={i}>{note.message}</li>
            ))}
          </ul>
        </div>

        <div className="dashboard-section">
          <h3>üí≥ Billing History</h3>
          <ul>
            {billingHistory.map((bill, i) => (
              <li key={i}>{bill.date} - {bill.service}: {bill.amount}</li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}
