import axios from "axios";

const api = axios.create({
  baseURL: "http://localhost:8080",
  withCredentials: true, // Always send cookies (JWT, refresh)
  xsrfCookieName: "XSRF-TOKEN",
  xsrfHeaderName: "X-XSRF-TOKEN"
});

// ✅ Axios Interceptor for auto refresh on 401
api.interceptors.response.use(
  (response) => response,
  async (error) => {
    const originalRequest = error.config;

    const excludedUrls = ["/auth/login", "/auth/register", "/auth/refresh-token"];
    const isExcluded = excludedUrls.some(url => originalRequest.url.includes(url));
    if (
      error.response.status === 401 &&
      !originalRequest._retry &&
      !originalRequest.url.includes("/auth/refresh-token") // ⛔ Prevent looping
    && !isExcluded) {
      originalRequest._retry = true;

      try {
        await axios.post("http://localhost:8080/auth/refresh-token", {}, {
          withCredentials: true
        });

        return axios(originalRequest);
      } catch (refreshError) {
        return Promise.reject(refreshError);
      }
    }


    return Promise.reject(error);
  }
);

export default api;
