import React, { useState } from "react";
import Navbar from "../MainPage/Navbar";
import { useNavigate } from "react-router-dom";
import "../../css/signup.css";
import axios from "axios";

export default function SignUp() {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    email: "",
    firstName: "",
    lastName:"",
    password: "",
  });
  const [message, setMessage] = useState("");

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.firstName || !formData.lastName || !formData.email || !formData.password) {
      setMessage("‚ö†Ô∏è Please fill in all fields.");
      return;
    }


    // üß™ Uncomment below when backend is ready

    console.log(formData);

    try {
      const res = await axios.post("http://localhost:8080/register/registerPatient", formData, formData, {
        headers: {
          "Content-Type": "application/json"
        }});
      if (res.status === 201 || res.status === 200) {
        setMessage(res.data.message || "‚úÖ Sign Up Successful!");
        setTimeout(() => navigate("/login"), 2000);
      }
    } catch (err) {
      const errorMsg =
        err.response?.data?.message || "‚ùå Error creating account. Try again.";
      setMessage(errorMsg);
    }

  };

  return (
    <>
      <Navbar />
      <div className="signup">
        <div className="signup-body">
          <div className="card signup-card">
            <div className="card-body">
              <h2 className="signup-title">Patient Sign Up</h2>
              {message && <div className="signup-message">{message}</div>}
              <form onSubmit={handleSubmit} className="signup-form">
                <input
                  type="text"
                  name="firstName"
                  placeholder="First Name"
                  className="input-sec"
                  onChange={handleChange}
                />
                <input
                  type="text"
                  name="lastName"
                  placeholder="Last Name"
                  className="input-sec"
                  onChange={handleChange}
                />
                <input
                  type="email"
                  name="email"
                  placeholder="Email Address"
                  className="input-sec"
                  onChange={handleChange}
                />
                <input
                  type="password"
                  name="password"
                  placeholder="Create Password"
                  className="input-sec"
                  onChange={handleChange}
                />
                <button type="submit" className="btn sub-btn btn-outline-primary">
                  Create Account
                </button>
              </form>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}
