import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "../../css/login.css";
import Navbar from "../MainPage/Navbar";

export default function Login() {
  const navigate = useNavigate();
  const [userName, setUserName] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");
  const [errorClass, setErrorClass] = useState("");
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    if (name === "username") {
      setUserName(value);
    }
    if (name === "password") {
      setPassword(value);
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (userName === "" || password === "") {
      setMessage("⚠️ Please fill all the fields");
      setErrorClass("error-box shake");
      setTimeout(() => setErrorClass("error-box"), 500);
      return;
    }

    try {
      const response = await axios.post("http://localhost:8080/api/auth/login", {
        username: userName,
        password: password,
      });

      const role = response.data.role;
      setMessage("");
      setIsLoggedIn(true);

      setTimeout(() => {
        if (role === "doctor") {
          navigate("/doctorDashBoard");
        } else if (role === "patient") {
          navigate("/patientDashboard");
        }
      }, 2000);
    } catch (error) {
      setMessage("Invalid ID or Password");
      setErrorClass("error-box shake");
      setTimeout(() => setErrorClass("error-box"), 500);
    }
  };

  return (
    <>
      <Navbar />
      <div className="login">
        <div className="login-body">
          {isLoggedIn ? (
            <div className="success-screen">
              <b>Login Successful</b>
              <p>Redirecting...</p>
            </div>
          ) : (
            <div className="card login-card">
              <div className="card-body">
                <h1 className="login-title">Log In</h1>
                {message && <div className={errorClass}>{message}</div>}
                <form className="login-form" onSubmit={handleSubmit}>
                  <input
                    type="text"
                    name="username"
                    placeholder="Enter ID"
                    className="input-sec"
                    style={{ borderRadius: "5px" }}
                    onChange={handleInputChange}
                  />
                  <input
                    type="password"
                    name="password"
                    placeholder="Enter Key"
                    className="input-sec"
                    style={{ borderRadius: "5px" }}
                    onChange={handleInputChange}
                  />
                  <button type="submit" className="btn submit-button btn-outline-success m-4">
                    Submit
                  </button>
                </form>
                <p className="mt-3">
                  Don't have an account? <a href="/signup">Sign Up as Patient</a>
                </p>
              </div>
            </div>
          )}
        </div>
      </div>
    </>
  );
}
